import { useResource } from "./resource.hook";

export const BookInfo = ({ bookId }) => {
  const book = useResource(`/books/${bookId}`);
  const { name, price, title, pages } = book || {};

  return book ? (
    <>
      <h3>{name}</h3>
      <p>{price}</p>
      <h3>Title: {title}</h3>
      <p>Number of Pages: {pages}</p>
    </>
  ) : (
    <h1>Loading</h1>
  );
};
